INSERT INTO `goods` (`good_id`, `good_name`, `price`, `good_type_id`, `description`, `image`) VALUES
(1, 'Intel Core i5-4460 3.2GHz/5GT/s/6MB (BX80646I)', 5311, 1, 'Процессор Intel Core i5-4460 основан на новой архитектуре Haswell Refresh. Номинальная внутренняя частота процессора составляет 3.2 ГГц, которая в режиме "турбо" увеличивается до 3.4 ГГц.', NULL),
(2, 'AMD FX-6300 3.5GHz/5200MHz /8MB (FD6300WMHKBO)', 2764, 1, 'AMD FX-6300 — это процессор, который основанный на микроархитектуре Piledriver и изготовленный по 32 нм техпроцессу. Частота процессора, оснащенного ядрами Vishera, — 3.5 ГГц. Имеется поддержка режима Turbo, повышающая частоту до 4.1 ГГц. Процессор имеет 6 МБ кеша второго уровня и 8 МБ кеша третьего уровня и оснащен новыми энергосберегающими технологиями. Тепловыделение процессора составляет 95 Вт.\r\n', NULL),
(4, 'Asus H81M-K (s1150, Intel H81, PCI-Ex16)', 1235, 3, '<p><b>H81M-K</b> — это mATX материнская плата от <b>Asus</b>, которая предлагает функцию разгона интегрированной графики, интуитивно понятный UEFI BIOS и оснащена высокопроизводительными портами USB 3.0.</p><p><b>Особенности:</b></p><ul><li><b><i>Технология GPU Boost – разгон графического ядра</i></b></li></ul><p>В новых материнских платах Asus реализована поддержка функции GPU Boost, с помощью которой можно моментально разогнать встроенное графическое ядро и получить солидную прибавку скорости. Причем параметры разгона – частота и напряжение питания ядра – регулируются с помощью удобного программного интерфейса. Для безопасного разгона графического ядра можно воспользоваться одним из предварительно созданных профилей разгона.</p><ul><li><b><i>UEFI BIOS с удобным интерфейсом</i></b></li></ul><p>Asus UEFI BIOS представляет собой реализацию стандарта UEFI (Unified Extensible Firmware Interface – Унифицированный расширяемый интерфейс прошивки), который пришел на смену традиционным системам BIOS. Одним из видимых невооруженным взглядом преимуществ Asus UEFI BIOS является поддержка не только клавиатуры, но и мыши. Кроме того, специально для неопытных пользователей предлагается упрощенный режим работы (EZ Mode), в котором можно легко и быстро задать базовые настройки компьютерной системы.</p><ul><li><b><i>USB 3.0 с поддержкой UASP</i></b></li></ul><p>Для ускорения интерфейса USB 3.0 специалисты Asus реализовали поддержку протокола UASP. При использовании совместимого периферийного устройства скорость передачи данных по USB возрастает до 170%. Для работы с функцией USB 3.0 Boost имеется удобный графический интерфейс.</p><ul><li><b><i>Q-Slot</i></b></li></ul><p>Удобная защелка графического слота позволяет с удобством устанавливать и вынимать любую видеокарту.</p><ul><li><b><i>Контроллер EPU</i></b></li></ul><p>Специальный чип для управления системой питания материнской платы. Активируется с помощью переключателя на самой плате или посредством программного комплекса AI Suite 3.</p><ul><li><b><i>Гибкие настройки загрузчика ОС, удобное конфигурирование процесса загрузки</i></b></li></ul><p>С помощью удобного интерфейса BIOS пользователь может указать предпочтительный метод загрузки: ускоренный (занимающий всего пару секунд) или обычный. Заданный метод будет применяться при всех последующих загрузках операционной системы.</p><ul><li><b><i>AI Suite 3</i></b></li></ul><p>Все эксклюзивные функции и технологии Asus теперь доступны посредством единого программного интерфейса Asus AI Suite 3. С его помощью можно осуществлять разгон компьютера, управлять параметрами энергопотребления и вентиляторами, изменять напряжения и следить за состоянием системы. Единый интерфейс избавляет пользователя от необходимости переключаться между дюжиной разных утилит.</p><ul><li><b><i>Функция Network iControl</i></b></li></ul><p>Программная утилита Network iControl позволяет задать приоритеты использования пропускной способности сетевого соединения различными приложениями с помощью удобного пользовательского интерфейса. Это легкий в использовании инструмент для управления сетевым контроллером компьютера.</p>', NULL),
(5, 'Asus M5A78L-M LX3 (sAM3+, AMD 760G, PCI-Ex16)', 1855, 3, '<p><b>Поддержка процессоров AMD FX / Phenom II / Athlon II / Sempron 100 (Socket AM3+)</b><br>Материнская плата предназначена для установки многоядерных процессоров AMD AM3+ с уникальным кэшем третьего уровня, в том числе, процессоров изготовленных по новому 32-нм технологическому процессу, обеспечивая экономичность энергопотребления и прекрасные возможности для разгона. Плата использует высокоскоростную системную шину HyperTransport 3.0 с пропускной способностью до 5200 млн. транзакций в секунду и может работать с двухканальной памятью DDR3.</p><p><b>Снижение энергопотребления компонентов системы</b><br>ASUS EPU – это технология энергосбережения, которая в режиме реального времени определяет уровень вычислительной нагрузки на систему и в соответствии с ней регулирует потребление энергии, тем самым добиваясь ее экономии. EPU автоматически регулирует параметры работы процессора, видеокарты, жестких дисков и системы охлаждения процессора.</p><p><b>Функция Core Unlocker</b><br>Функция ASUS Core Unlocker включает заблокированные ядра процессора AMD — для этого достаточно нажатия одной клавиши. Добейтесь мгновенного ускорения, быстро разблокировав незадействованные ядра процессора без необходимости копаться в BIOS платы!</p>', NULL),
(6, 'Aerocool PGS V3X Advance Black Edition + Aero', 1629, 4, '<b>Aerocool PGS V3X Advance Black Edition</b> разработан в дружественной для пользователя концепции, которая дает геймерам большую свободу действий для апгрейда и обслуживания системы.</p><p><b>Особенности:</b></p><ul><li>Поддержка габаритных видеокарт длиной до 35 см</li><li>Встроенный 120 мм черный вентилятор с красной подсветкой на фронтальной панели</li><li>Встроенный 80 мм вентилятор на задней панели</li><li>Гибкая система крепления HDD (возможность установки как в вертикальном положении, так и в горизонтальном)</li><li>Верхнее расположение блока питания</li><li>7 слотов расширения</li><li>Отсеки для накопителей: 3 x 5.25" / 4 x 3.5" HDD / 2 x 2.5" HDD</li></ul><p> <br>Укомплектован блоком питания <b>Aerocool VХ-550.</b><br> <br><b>Особенности блока питания:</b></p><ul><li>Совместим с ATX 12 В 2.3</li><li>Бесшумный 12-см вентилятор</li><li>Поддержка режимов энергосбережения C6/C7 для процессоров Intel Haswell</li><li>Длинные кабели (450 мм) позволяют устанавливать блок питания в нижней части корпусов</li><li>Активная коррекция коэффициента мощности</li><li>Низкий уровень пульсаций и шумов</li><li>Защита от перегрузки</li><li>Защита от перенапряжения</li><li>Защита от короткого замыкания </li><li>Защита от пониженного напряжения</li></ul><br><p><b>Гарантия на корпус — 14 дней, на блок питания — 24 месяца.</b></p>', NULL),
(7, 'Crown SM158 500 Вт Black Silver', 1249, 4, '<p>Стильный компьютерный корпус <b>Crown SM158 </b>с глянцевой фронтальной панелью из пластика высокой прочности. Прочная конструкция корпуса надежно фиксирует компьютерные комплектующие и защищает их от внешних физических воздействий. На передней панели расположены 2 USB порта и разъемы MIC и SPK.<br> <br><b>Особенности:</b></p><ul><li>Стильная глянцевая фронтальная панель</li><li>Завальцованные края корпуса гарантируют безопасную и удобную работу при конфигурировании компьютера</li><li>Металлическое шасси покрыто специальным напылением для защиты от электромагнитного излучения элементов компьютера</li><li>Ребра жесткости увеличивают прочность конструкции</li><li>Заводская сборка обеспечивает высокое качество продукта</li><li>Розничная противоударная упаковка и полиэтиленовый защитный пакет — сохранят товарный вид изделия от влаги и технических повреждений</li></ul>', NULL),
(8, 'Asus PCI-Ex GeForce GTX 950 2048MB GDDR5 (128)', 4921, 2, '<p><b>Asus GTX 950</b> – это особенная видеокарта с привлекательным белым оформлением, двухвентиляторной системой охлаждения и премиальными компонентами <i>Super Alloy Power II</i>. Она совместима с эксклюзивной утилитой для мониторинга и разгона <i>Asus GPU Tweak II</i>.</p><p><b>Стиль и скорость</b><br>Выполненные в едином стиле, видеокарта <i>Asus GTX 950</i> и материнские платы Asus серии Z170 прекрасно подходят для совместной работы, в том числе, на компьютерах с новой операционной системе Windows 10. Данная видеокарта обладает поддержкой технологии DirectX 12.</p><p><b>Система охлаждения с двумя вентиляторами</b><br>На данную видеокарту устанавливается система охлаждения с двумя вентиляторами, обеспечивающими более мощный поток воздуха и пониженную температуру по сравнению с референсным кулером при значительно меньшем уровне шума.</p><p><b>Безупречное качество и надежность</b><br>Стабильной работе видеокарт способствует высококачественная элементная база и эксклюзивная технология Auto-Extreme, которая подразумевает первый в индустрии на 100% автоматизированный процесс производства. Компоненты <i>Super Alloy Power II</i> обеспечивают эффективное потребление энергии, значительно уменьшенный уровень вибраций и шума при максимальной нагрузке и снижение рабочей температуры. </p><p><b>Эксклюзивная утилита для разгона</b><br>Новая версия <i>GPU Tweak</i> имеет такой же удобный интерфейс, как и предыдущие, а также включает фунцию <i>Game Booster</i> для настройки максимального быстродействия игровой графики и <i>XSplit Gamecaster</i> для записи и трансляции процесса игры.</p><p><b>XSplit Gamecaster: трансляция и запись процесса игры</b><br>С помощью <i>XSplit Gamecaster</i> можно не только транслировать игровое видео в Интернет, но и записывать процесс игры. Игроки имеют возможность рисовать поверх видео и комментировать игру.</p>', NULL),
(9, 'MSI PCI-Ex Radeon R9 390X 8192MB GDDR5 (512bi)', 13307, 2, '<p>Графическая карта является самым важным элементом в достижении высоких FPS. Вот почему MSI представляет вам лучшее из лучшего. Беспрецедентно высокая производительность — <b>MSI</b> <b>R9 390X Gaming</b>.</p><p><b>Приложение Gaming APP</b></p><ul><li><b>Режим OC Mode.</b> Настройте частоту ядра и скорости вращения вентилятора, чтобы получить легкость в игре.</li><li><b>Режим Gaming Mode (по умолчанию).</b> Обеспечивает стабильную работу на высоких частотах.</li><li><b>Режим Silent Mode.</b> Лучшие настройки для самого низкого уровня уровня шума.</li></ul><br><p><b>Twin Frozr</b><br>Twin Frozr предопределила развитие целой индустрии созданием системы охлаждения для видеокарт. Первый Twin Frozr породил двухвентиляторный тренд, и MSI по сей день следует ему, выпуская двухвентиляторные двухслотовые системы охлаждения.</p><p><b>Airflow Control</b><br>Специальные дефлекторы направляют больший объем воздуха напрямую на тепловые трубки, тем самым позволяя намного быстрее рассеивать тепло. В дополнение, переработан весь дизайн СО и увеличена площадь рассеивания радиатора, что делает эту систему охлаждения практически бесшумной в любых режимах работы.</p><p><b>Afterburner</b><br>Afterburner быстро стал любимым программным обеспечением для разгона видеокарт у журналистов, оверклокеров и обычных пользователей. Благодаря простому интерфейсу им легко пользоваться, но в то же время он содержит несметное количество мощных функций, таких как встроенный бенчмарк Kombustor, который может работать как в режиме теста на стабильность, так и на производительность.</p><p><b>Компоненты Military Class</b><br>Одним из решающих факторов в производительности является качество используемых компонентов. Вот почему MSI использует только MIL-STD-810G сертифицированные компоненты для серии Lightning, ведь только эти компоненты оказались в состоянии выдержать напряженнейшие условия экстремального разгона.</p><p><b>Solid CAP — Твердотельные конденсаторы</b><br>Твердотельные конденсаторы Solid CAP с их алюминиевым сердечником уже долгое время являются одним из определяющих факторов в дизайне высококачественных материнских плат, так как обеспечивают более низкое эквивалентное последовательное сопротивление (ESR), а также 10-летний срок службы.</p><p><b>Конденсаторы Hi-c CAP</b><br>Конденсаторы Hi-c CAP — это очень маленькие, но невероятно эффективные конденсаторы. Помимо обеспечения достаточного пространства вокруг процессорного разъема для установки больших кулеров они также расходуют на 93% меньше энергии. Именно поэтому платы MSI являются наиболее энергоэффективными на рынке.</p><p><b>Новые Super Ferrite Choke</b><br>Новые Super Ferrite Choke (SFC) — это катушки с сердечником из феррита с суперпроводимостью. Это позволяет SFC работать в среднем с более низкой температурой, пропускать больше тока и быть более энергоэффективными, тем самым обеспечивая лучшую стабильность во время разгона процессора.</p>', NULL),
(10, 'Kingston DDR3-1600 4096MB PC3-12800 (KVR16N11)', 606, 5, 'DDR3 сейчас является доминирующим типом памяти для портативных и настольных компьютеров, серверов и рабочих станций. DDR3 рассчитана на работу на более высоких частотах, чем DDR, и характеризуется меньшим энергопотреблением.', NULL),
(11, 'Kingston DDR3-1600 8192MB PC3-12800 (KVR16N11)', 1001, 5, 'DDR3 сейчас является доминирующим типом памяти для портативных и настольных компьютеров, серверов и рабочих станций. DDR3 рассчитана на работу на более высоких частотах, чем DDR, и характеризуется меньшим энергопотреблением.', NULL),
(12, 'Seagate Barracuda 7200.12 500GB ', 1275, 6, '<p>12-е поколение жестких дисков Barracuda продолжает давнюю традицию технологического лидерства компании Seagate, результатом которой являются новые уровни емкости, улучшенное быстродействие и более высокая надежность. </p><p>Диск отличается высоким быстродействием и большой емкостью для обеспечения работы современных приложений, требующих хранения больших объемов данных.</p><p>Жесткий диск Barracuda подходит для решения любых задач на настольных системах и имеет емкость <b>500 ГБ</b>. </p>', NULL),
(13, 'Western Digital My Passport Ultra 2TB WDBBKD0020BBK-EESN 2.5 USB 3.0 Black', 1619, 6, '<p>Несмотря на свои компактные размеры, накопитель <b>My Passport Ultra</b> является стильным, мощным и защищенным устройством. Под цветным корпусом скрыты надежность и результат семи поколений инноваций. Миллионы людей по всему миру выбрали компактный накопитель <b>My Passport</b>. </p><p>Накопитель обладает гибкими параметрами резервного копирования и аппаратным шифрованием с 256-разрядным ключом.</p><p><b>WD Backup: автоматическое и масштабируемое резервное копирование</b><br>Создайте собственную стратегию автоматического резервного копирования, отвечающую вашему графику и стилю работы. Создавайте резервные копии всех файлов системы или только отдельных папок и файлов. </p><p><b>Локальное и облачное резервное копирование</b><br>Самостоятельно выбирайте хранилище резервных файлов: накопитель <b>My Passport Ultra </b>или учетную запись облачной службы Dropbox или Baidu Cloud.<br><i>* Для облачного резервного копирования требуется учетная запись Dropbox или Baidu Cloud. Облачные службы могут быть доступны не во всех странах, а также могут быть изменены, прекращены или прерваны в любой момент</i>.</p><p><b>WD Security: секретный уровень защиты</b><br>Вы ведь защищаете данные на телефоне с помощью пароля? Почему бы не использовать такую же защиту в накопителе <b>My Passport</b>? Выберите пароль для защиты с помощью мощного аппаратного шифрования с 256-разрядным ключом, которое используется правительствами для защиты секретной информации.</p><p><b>Питание через интерфейс USB 3.0</b><br>Накопитель <b>My Passport Ultra</b> поддерживает интерфейс USB 3.0, обеспечивающий скорость передачи данных 5 Гбит/с (что в три раза превышает скорость USB 2.0). Для работы накопителя <b>My Passport Ultra</b> не нужны громоздкие шнуры питания и штепсельные вилки.</p><p><b>WD Drive Utilities</b><br>Зарегистрируйте накопитель, настраивайте таймер холостого режима, проверяйте свой диск, оптимизируйте работу и настраивайте функции защиты накопителя с помощью удобной программы WD Drive Utilities.</p><p><b>Изящная внешность и большая емкость</b><br>Этот быстрый накопитель в корпусе толщиной всего 20.96 мм имеет достаточную емкость для хранения всех ваших важных файлов.</p><p><b>Надежность WD</b><br>В компании <b>WD</b> понимают, насколько важны ваши данные, поэтому они изготавляют накопители в соответствии с жесткими требованиями к долговечности, ударостойкости и неизменной надежности. </p>', NULL),
(14, 'Aerocool KCAS-700 700W (4713105953282)', 1449, 9, '<p><b>Aerocool KCAS-700</b> — блок питания форм-фактора ATX с выходной мощностью 700 Вт. Блок имеет систему коррекции мощности, систему охлаждения состоящую из 120 мм вентилятора и несколько уровней защиты.</p><p><b>Особенности:</b></p><ul><li>Непревзойденный по характеристикам выход постоянного тока 700 Вт</li><li>Блок питания в элегантном черном корпусе с кабелями в черной сетчатой оплетке</li><li>Соответствие стандарту ATX12V 2.3</li><li>Поддержка процессора «Haswell» последнего 4-го поколения Intel с режимом энергосбережения C6/C7</li><li>Эффективность составляет 85%+, это официально подтверждено сертификатом 80Plus Bronze</li><li>Одинарная шина питания 12 В обеспечивает высокую совместимость и невероятно высокую мощность выходного тока</li><li>Используются кабели постоянного тока только в сетчатой оплетке для оптимальной циркуляции воздуха и организации кабелей</li><li>Два 6+2 контактный разъем PCIe для средней видеокарты</li><li>7 разъемов SATA обеспечивают подключение нескольких жестких дисков</li><li>Бесшумный черный вентилятор 12 см с интеллектуальным тепловым контуром регулирования скорости</li><li>Полная защита от сверхтоков, перегрузки, перенапряжения, короткого замыкания и температуры (OVP/UVP/OPP/SCP/SIP)</li><li>Безопасность и электромагнитная совместимость подтверждена сертификатами CE и TUV</li><li>Устройство экологически безопасно и соответствует требованиям директив RoHS и Erp 2013</li></ul>\r\n', NULL),
(15, 'DeepCool Explorer 480W (DE480)', 658, 9, '<p><b>DeepCool Explorer </b>— блок питания форм-фактора ATX с пиковой выходной мощностью 480 Вт (номинальная выходная мощность 350 Вт). Блок оборудован системой охлаждения из 120 мм вентилятора и несколькими уровнями защиты.</p><ul><li>Совместимость со стандартом Intel ATX12V v 2.31</li><li>Бесшумный 120-мм вентилятор с поддержкой ШИМ-управления</li><li>Технология CircuitShield защищает блок питания от перегрузки по напряжениям (OVP), проседания напряжений (UVP), коротких замыканий (SCP) и перегрузки по общей мощности на всех каналах (OPP)</li><li>Не содержит свинца, соответствует стандарту RoHS</li><li>Среднее время наработки на отказ (MTBF) составляет не меньше 100 000 часов</li><li>Отличное соотношение цены и производительности</li></ul><br><p><b>Умный 120-мм вентилятор</b><br>Отличное соотношение производительности и шума. </p><p><b>Безопасность и долгий срок службы</b><br>Многоуровневая защита и срок службы не менее 100 000 часов. </p>', NULL),
(28, 'Intel Core i7-6700K 4.0GHz/8GT/s/8MB (BX80662I76700K) s1151 BOX', 11699, 1, '																																									<p>Новый процессор <b>Intel Core i7-6700K</b> 6-го поколения, с кодовым названием микроархитектуры <b>Skylake</b>. Предназначен для настольной платформы <b>Intel LGA 1151</b>. Принадлежит к семейству высокопроизводительных процессоров <b>Core i7</b> с большим разгонным потенциалом.</p><p><b>Intel Core i7-6700K</b> производится по стандарту 14-нм техпроцесса, имеет 4 ядра, которые работают в 8 потоков со штатной тактовой частотой 4.0 ГГц, 4.2 ГГц в режиме <b>Turbo Boost 2.0</b>. Объем кэш-памяти 3 уровня равен 8 МБ. Имеет 2-х канальный контроллер памяти DDR4 / DDR3L и разблокированный множитель.</p><p><b>Особенности:</b></p><ul><li>Intel Hyper-Threading – работа над двумя задачами одновременно, поможет выполнить больше за меньшее время</li><li>Встроенное графическое ядро нового поколения Intel HD Graphics 530 с максимальной динамической частотой системы до 1150 МГц</li><li>Ultra HD 4K – наслаждайтесь удивительным и динамичным видео на ваших Ultra HD и 4K дисплеях (разрешение до 4096 х 2304)</li><li>Intel Quick Sync Video дает отличную возможность видеоконференций, быстрого преобразования, редактирования и авторизации видео, а также онлайн обмена</li><li>OpenCL 2.0 – теперь программисты могут легко воспользоваться вычислительными мощностями графического ядра</li><li>Встроенный DirectX 12 API обеспечит наилучшие впечатление от игр и графики</li></ul><br><p><b>Преимущества 6-го поколения над 4 (Haswell Refresh):</b></p><ul><li>Выше энергоэффективность</li><li>Выше процессорная производительность</li><li>Более производительное графическое ядро Gen 9</li><li>Аппаратная поддержка новых кодеков x.265, VP9, VP8</li><li>Улучшенная схема разгона</li><li>Поддержка OpenCL2.0 (SVM)</li><li>Новые ISA для безопасности</li></ul><br><p><b>Технологии защиты:</b></p><ul><li>Intel Software Guard Extensions</li><li>Intel Advanced Encryption Standard Instructions</li><li>Intel Secure Key</li><li>Intel OS Guard</li><li>Intel Identity Protection Technology</li><li>Intel Device Protection Technology with Boot Guard</li></ul><br><p><b>Интеграция 6 поколения:</b></p><ul><li>Системы охлаждения для 4 поколения совместимы с 6 (крепление)</li><li>Рекомендованная проектная мощность системы охлаждения (FHS) для этих моделей 130 Вт</li></ul><br><p><b>Внимание! Процессор поставляется без системы охлаждения.</b></p>', NULL),
(29, 'AMD FX-8320 3.5GHz/5200MHz/8MB', 4050, 1, 'AMD FX-8320 — восьмиядерный процессор основанный на микроархитектуре Vishera изготовленный по 32 нм техпроцессу. Частота процессора — 3.5 ГГц. Включена поддержка режима Turbo повышающая частоту до 4 ГГц. Процессор имеет 8 МБ кеша второго уровня и 8 МБ кеша третьего уровня. Тепловыделение процессора составляет 125 Вт.', NULL),
(30, 'MSI PCI-Ex Radeon R9 380 2048MB', 6555, 2, '<p>Графическая карта является самым важным элементом в достижении высоких FPS. Вот почему MSI представляет вам лучшее из лучшего. Беспрецедентно высокая производительность — <b>MSI</b> <b>R9 380 Gaming</b>.</p><p><b>Приложение Gaming APP</b></p><ul><li><b>Режим OC Mode.</b> Настройте частоту ядра и скорости вращения вентилятора, чтобы получить легкость в игре.</li><li><b>Режим Gaming Mode (по умолчанию).</b> Обеспечивает стабильную работу на высоких частотах.</li><li><b>Режим Silent Mode.</b> Лучшие настройки для самого низкого уровня уровня шума.</li></ul><br><p><b>Twin Frozr</b><br>Twin Frozr предопределила развитие целой индустрии созданием системы охлаждения для видеокарт. Первый Twin Frozr породил двухвентиляторный тренд, и MSI по сей день следует ему, выпуская двухвентиляторные двухслотовые системы охлаждения.</p><p><b>Airflow Control</b><br>Специальные дефлекторы направляют больший объем воздуха напрямую на тепловые трубки, тем самым позволяя намного быстрее рассеивать тепло. В дополнение, переработан весь дизайн СО и увеличена площадь рассеивания радиатора, что делает эту систему охлаждения практически бесшумной в любых режимах работы.</p><p><b>Afterburner</b><br>Afterburner быстро стал любимым программным обеспечением для разгона видеокарт у журналистов, оверклокеров и обычных пользователей. Благодаря простому интерфейсу им легко пользоваться, но в то же время он содержит несметное количество мощных функций, таких как встроенный бенчмарк Kombustor, который может работать как в режиме теста на стабильность, так и на производительность.</p><p><b>Компоненты Military Class</b><br>Одним из решающих факторов в производительности является качество используемых компонентов. Вот почему MSI использует только MIL-STD-810G сертифицированные компоненты для серии Lightning, ведь только эти компоненты оказались в состоянии выдержать напряженнейшие условия экстремального разгона.</p><p><b>Solid CAP — Твердотельные конденсаторы</b><br>Твердотельные конденсаторы Solid CAP с их алюминиевым сердечником уже долгое время являются одним из определяющих факторов в дизайне высококачественных материнских плат, так как обеспечивают более низкое эквивалентное последовательное сопротивление (ESR), а также 10-летний срок службы.</p><p><b>Конденсаторы Hi-c CAP</b><br>Конденсаторы Hi-c CAP — это очень маленькие, но невероятно эффективные конденсаторы. Помимо обеспечения достаточного пространства вокруг процессорного разъема для установки больших кулеров они также расходуют на 93% меньше энергии. Именно поэтому платы MSI являются наиболее энергоэффективными на рынке.</p><p><b>Новые Super Ferrite Choke</b><br>Новые Super Ferrite Choke (SFC) — это катушки с сердечником из феррита с суперпроводимостью. Это позволяет SFC работать в среднем с более низкой температурой, пропускать больше тока и быть более энергоэффективными, тем самым обеспечивая лучшую стабильность во время разгона процессора.</p>', NULL),
(31, 'SSD Kingston SSDNow V300 120GB 2.5', 1274, 7, '<p>Повысьте скорость работы вашего компьютера и сэкономьте средства, заменив старый жесткий диск на твердотельный накопитель SSDNowV300 компании Kingston. Это самое экономически эффективное решение для повышения производительности системы и более простой способно сравнению с переносом всех данных на новую систему. SSDNow V300 имеет контроллер LSI SandForce, оптимизированный для флэш-памяти нового поколения, что обеспечивает высочайшее качество и надежность. Накопители состоят из твердотельных компонентов и не содержат движущихся деталей, поэтому они имеют повышенную ударопрочность и выдерживают падения и вибрации. Накопители SSDNow V300 просты в установке и поставляются в комплектах, содержащих все необходимые аксессуары, включая программное обеспечение для клонирования файлов и операционной системы за считанные минуты. Они имеют трехлетнюю гарантию, бесплатную техническую поддержку и отличаются легендарной надежностью Kingston.</p><p><b>Преимущества:</b></p><ul><li>Скорость — повышает скорость запуска приложений и загрузки системы</li><li>Надежность — твердотельный накопитель не содержит движущихся частей, поэтому вероятность его отказа меньше, чем у жесткого диска</li><li>Экономичность — конструкция оптимизирована для удобства перехода</li><li>Удобство использования — универсальные комплекты включают в себя все необходимые компоненты для простой установки</li><li>Различные варианты емкости — соответствует любым потребностям пользователей</li><li>Форм-фактор — 2.5-дюймовый накопитель высотой 7 мм с адаптером для установки в отсеки высотой 9.5 мм</li><li>Бесшумность — бесшумная работа и низкое тепловыделение</li><li>Гарантия — трехлетняя гарантия, бесплатная техническая поддержка</li></ul><br><p>Гарантия на SSD-накопители Kingston составляет 3 года (КС100 — 5 лет), включая срок службы (износ) ячеек.</p><p>Kingston гарантирует, что если пользователь записывает 20 ГБ информации в день, на накопитель объемом 60 ГБ ячейки такого накопителя прослужат 3 года. При росте объема накопителя в той же пропорции растет возможный объем записи в день или срок службы. Например, если объем накопителя составляет 120 ГБ на него можно записывать 20 ГБ в день на протяжении 6 лет или 40 ГБ в день на протяжении 3 лет до окончания срока службы ячеек.</p><p>В случае окончания срока службы ячеек, SSD-накопитель блокируется на запись, но не теряет информацию. Срок службы ячеек можно мониторить в реальном времени через SMART-атрибуты накопителя. </p>', NULL),
(32, 'Zalman CNPS10X Performa+', 925, 8, '<p>Процессорный кулер<b> Zalman CNPS10X Performa+ </b>оснащен блоком алюминиевых ребер и пятью медными теплоотводными трубками, сходящимися у основания и проходящими насквозь радиаторный блок. </p><p>Кулер представлен радиатором «башенного» конструктива, который в процессе инсталляции дополняется комплектным вентилятором типоразмера 120 х 120 х 25 мм (допускается установка еще одного опционального вентилятора). </p><p>Поставка включает радиатор, фирменный вентилятор, комплект мультиплатформенного крепежа (поддерживаются все актуальные платформы — <b>Intel LGA775/LGA1151/LGA1156/LGA1366/2011</b> и <b>AMD Socket AM2/AM3)</b>, крепежные скобки для штатного и опционального вентиляторов (две пары), пакетик с термопастой, фирменный стикер и техническое руководство.</p><p><b>Размеры: </b>131 х 96.5 х 151 мм<br><b>Вес: </b>713 г (без учета крепежа)</p>', NULL);
